	public static void cantidadPisos(String pisos){
		int cantidadPisos = 0;

        for (int i = 0; i < pisos.length(); i++) {
            if (pisos.charAt(i) == '(') cantidadPisos++;
			if (pisos.charAt(i) == ')') cantidadPisos--;
        }

		System.out.println("Cantidad de pisos recorridos: " + cantidadPisos);
	}

	public  static void cantidadRegistros(String pisos){
		int cantidadPisos = 0;
		int cantidadRegistros = 0;

        for (int i = 0; i < pisos.length(); i++) {
			if(cantidadPisos != -1){
				if (pisos.charAt(i) == '(') cantidadPisos++;
				if (pisos.charAt(i) == ')') cantidadPisos--;
				cantidadRegistros++;
			}

        }

		System.out.println("Cantidad de registros del primero en el sotano: " + cantidadRegistros);
	}
